@Composable
fun ExchangeScreen(modifier: Modifier = Modifier, vm: ExchangeViewModel = viewModel()) {
 val state by vm.state.collectAsState()
 val scroll = rememberScrollState()
 var showDialog by remember { mutableStateOf(false) }
 var dialogMessage by remember { mutableStateOf("") }

 Column(
 modifier = modifier
 .fillMaxSize()
 .verticalScroll(scroll)
 .padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 // Header
 Box(
 modifier = Modifier
 .fillMaxWidth()
 .background(Color(0xFF0293D6))
 .padding(vertical = 16.dp),
 contentAlignment = Alignment.Center
 ) {
 Text("Currency converter", color = Color.White, fontWeight = FontWeight.SemiBold)
 }

 Text("MY BALANCES", style = MaterialTheme.typography.labelSmall, color = Color.Gray)
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .horizontalScroll(rememberScrollState()),
 horizontalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 state.balances.keys.sorted().forEach { code ->
 Text(
 text = vm.formattedBalance(code),
 fontWeight = FontWeight.Medium,
 modifier = Modifier.padding(end = 8.dp)
 )
 }
 }

 Text("CURRENCY EXCHANGE", style = MaterialTheme.typography.labelSmall, color = Color.Gray)

 // Sell
 ExchangeRow(
 directionColor = Color(0xFFE74C3C),
 directionIcon = "↑",
 label = "Sell",
 amount = state.inputAmount,
 onAmountChanged = vm::setInputAmount,
 currency = state.sellCurrency,
 currencies = vm.availableCurrencies(),
 onCurrencyChanged = vm::setSellCurrency
 )

 Divider()

 // Receive
 val quote = vm.computeQuote()
 ExchangeRow(
 directionColor = Color(0xFF2ECC71),
 directionIcon = "↓",
 label = "Receive",
 amount = quote?.let { "+%.2f".format(it) } ?: "",
 onAmountChanged = {},
 currency = state.buyCurrency,
 currencies = vm.availableCurrencies(),
 onCurrencyChanged = vm::setBuyCurrency,
 readOnly = true
 )

 Spacer(Modifier.height(16.dp))

 ElevatedButton(
 onClick = {
 if (vm.performExchange()) {
 val sold = state.inputAmount.toDoubleOrNull() ?: 0.0
 val received = quote ?: 0.0
 val fee = sold * 0.007 // sample fee to match screenshot
 dialogMessage = "You have converted %.2f %s to %.2f %s. Commission Fee: %.2f %s.".format(
 sold, state.sellCurrency, received, state.buyCurrency, fee, state.sellCurrency
 )
 showDialog = true
 }
 },
 enabled = vm.canExchange(),
 modifier = Modifier
 .fillMaxWidth()
 .clip(RoundedCornerShape(28.dp))
 ) { Text("SUBMIT") }

 if (state.isLoading) {
 Row(Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.Center) {
 CircularProgressIndicator(modifier = Modifier.padding(top = 8.dp))
 }
 }
 state.error?.let { Text(it, color = MaterialTheme.colorScheme.error) }
 }

 if (showDialog) {
 AlertDialog(
 onDismissRequest = { showDialog = false },
 title = { Text("Currency converted") },
 text = { Text(dialogMessage) },
 confirmButton = { TextButton(onClick = { showDialog = false }) { Text("Done") } }
 )
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun CurrencyDropdown(label: String, selected: String, options: List<String>, onSelected: (String) -> Unit) {
 var expanded by remember { mutableStateOf(false) }

 ExposedDropdownMenuBox(
 expanded = expanded,
 onExpandedChange = { expanded = !expanded }
 ) {
 TextField(
 readOnly = true,
 value = selected,
 onValueChange = {},
 trailingIcon = { ExposedDropdownMenuDefaults.TrailingIcon(expanded = expanded) },
 modifier = Modifier
 .menuAnchor()
 .width(80.dp),
 textStyle = MaterialTheme.typography.bodyMedium,
 colors = TextFieldDefaults.colors(
 focusedIndicatorColor = Color.Transparent,
 unfocusedIndicatorColor = Color.Transparent,
 disabledIndicatorColor = Color.Transparent
 )
 )
 ExposedDropdownMenu(
 expanded = expanded,
 onDismissRequest = { expanded = false }
 ) {
 options.forEach { option ->
 DropdownMenuItem(
 text = { Text(option) },
 onClick = {
 onSelected(option)
 expanded = false
 }
 )
 }
 }
 }
}

@Composable
private fun ExchangeRow(
 directionColor: Color,
 directionIcon: String,
 label: String,
 amount: String,
 onAmountChanged: (String) -> Unit,
 currency: String,
 currencies: List<String>,
 onCurrencyChanged: (String) -> Unit,
 readOnly: Boolean = false
) {
 Row(
 modifier = Modifier.fillMaxWidth(),
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Left side - Direction icon and label
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Box(
 modifier = Modifier
 .clip(CircleShape)
 .background(directionColor)
 .padding(horizontal = 12.dp, vertical = 8.dp),
 contentAlignment = Alignment.Center
 ) { Text(directionIcon, color = Color.White, fontWeight = FontWeight.Bold) }

 Text(label, color = Color.Gray)
 }

 Spacer(modifier = Modifier.weight(1f))

 // Right side - Amount and currency
 if (readOnly) {
 // For receive row, show as text with currency dropdown
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 Text(
 text = amount,
 style = MaterialTheme.typography.bodyLarge,
 fontWeight = FontWeight.Medium
 )
 CurrencyDropdown(
 label = "",
 selected = currency,
 options = currencies,
 onSelected = onCurrencyChanged
 )
 }
 } else {
 // For sell row, show input field and currency dropdown
 Row(
 verticalAlignment = Alignment.CenterVertically,
 horizontalArrangement = Arrangement.spacedBy(8.dp)
 ) {
 TextField(
 value = amount,
 onValueChange = onAmountChanged,
 placeholder = { Text("0.00") },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Number),
 modifier = Modifier.width(100.dp),
 textStyle = MaterialTheme.typography.bodyLarge,
 colors = TextFieldDefaults.colors(
 focusedIndicatorColor = Color.Transparent,
 unfocusedIndicatorColor = Color.Transparent,
 disabledIndicatorColor = Color.Transparent
 )
 )
 CurrencyDropdown(
 label = "",
 selected = currency,
 options = currencies,
 onSelected = onCurrencyChanged
 )
 }
 }
 }
}

