@Composable
fun ExchangeScreen(modifier: Modifier = Modifier) {
 val context = LocalContext.current
 val factory = ExchangeViewModelFactory(context)
 val vm: ExchangeViewModel = viewModel(factory = factory)

 val state by vm.state.collectAsState()
 val scroll = rememberScrollState()
 var showDialog by remember { mutableStateOf(false) }
 var dialogMessage by remember { mutableStateOf("") }

 Box(
 modifier = modifier
 .fillMaxSize()
 .background(Color(0xFFE8EEF2))
 ) {
 Column(
 modifier = Modifier
 .fillMaxSize()
 .verticalScroll(scroll)
 .padding(16.dp),
 verticalArrangement = Arrangement.spacedBy(20.dp)
 ) {
 // Calculate quote at top level so it can be used everywhere
 val quote = vm.computeQuote()

 // Header
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 16.dp)
 ) {
 Text(
 text = "Currency Exchange",
 style = MaterialTheme.typography.headlineMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF2C3E50)
 )

 if (!state.isNetworkAvailable) {
 Spacer(modifier = Modifier.height(4.dp))
 Text(
 "⚠️ Offline - Rates may be outdated",
 color = Color(0xFFF39C12),
 style = MaterialTheme.typography.bodySmall,
 fontWeight = FontWeight.Medium
 )
 }
 }

 // MY BALANCES Section
 Column(
 modifier = Modifier.fillMaxWidth(),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Text(
 text = "MY BALANCES",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.SemiBold,
 color = Color(0xFF5D6D7E)
 )

 // Balance Cards
 Row(
 modifier = Modifier
 .fillMaxWidth()
 .clip(RoundedCornerShape(16.dp))
 .background(Color.White)
 .padding(20.dp),
 horizontalArrangement = Arrangement.SpaceEvenly
 ) {
 state.balances.keys.sorted().take(3).forEachIndexed { index, code ->
 if (index > 0) {
 Box(
 modifier = Modifier
 .width(1.dp)
 .height(60.dp)
 .background(Color(0xFFE0E0E0))
 )
 }

 Column(
 modifier = Modifier.weight(1f),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = code,
 style = MaterialTheme.typography.titleLarge,
 fontWeight = FontWeight.Medium,
 color = Color(0xFF5D6D7E)
 )
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = String.format("%.2f", state.balances[code] ?: 0.0),
 style = MaterialTheme.typography.headlineSmall,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF2C3E50)
 )
 }
 }
 }
 }

 // CURRENCY EXCHANGE Section
 Column(
 modifier = Modifier.fillMaxWidth(),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Text(
 text = "CURRENCY EXCHANGE",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.SemiBold,
 color = Color(0xFF5D6D7E)
 )

 // SELL Card
 ExchangeCard(
 label = "SELL",
 amount = state.inputAmount,
 onAmountChanged = vm::setInputAmount,
 currency = state.sellCurrency,
 currencies = vm.availableCurrencies(),
 onCurrencyChanged = vm::setSellCurrency,
 isReadOnly = false
 )

// Box(
// modifier = Modifier.fillMaxWidth(),
// contentAlignment = Alignment.Center
// ) {
// Box(
// modifier = Modifier
// .clip(CircleShape)
// .background(Color.White)
// .padding(12.dp)
// ) {
// Text(
// text = "⇅",
// fontSize = 24.sp,
// color = Color(0xFF5D6D7E)
// )
// }
// }

 // RECEIVE Card
 ExchangeCard(
 label = "RECEIVE",
 amount = quote?.let { String.format("%.10f", it) } ?: "0.00",
 onAmountChanged = {},
 currency = state.buyCurrency,
 currencies = vm.availableCurrencies(),
 onCurrencyChanged = vm::setBuyCurrency,
 isReadOnly = true
 )

 // Live Rate Display
 if (quote != null && state.inputAmount.toDoubleOrNull() != null) {
 val rate = if (state.inputAmount.toDoubleOrNull()!! > 0) {
 quote / state.inputAmount.toDoubleOrNull()!!
 } else {
 0.0
 }

 Text(
 text = "Live rate: 1 ${state.sellCurrency} = %.2f ${state.buyCurrency}".format(
 rate
 ),
 style = MaterialTheme.typography.bodyMedium,
 color = Color(0xFF5D6D7E),
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 textAlign = TextAlign.Center
 )
 }
 }

 Spacer(modifier = Modifier.height(8.dp))

 // SUBMIT Button
 ElevatedButton(
 onClick = {
 if (vm.performExchange()) {
 val sold = state.inputAmount.toDoubleOrNull() ?: 0.0
 val received = quote ?: 0.0
 val fee = sold * 0.007
 dialogMessage =
 "You have converted %.2f %s to %.2f %s. Commission Fee: %.2f %s.".format(
 sold,
 state.sellCurrency,
 received,
 state.buyCurrency,
 fee,
 state.sellCurrency
 )
 showDialog = true
 }
 },
 enabled = vm.canExchange(),
 modifier = Modifier
 .fillMaxWidth()
 .height(56.dp)
 .clip(RoundedCornerShape(28.dp)),
 colors = androidx.compose.material3.ButtonDefaults.elevatedButtonColors(
 containerColor = Color(0xFF2196F3),
 contentColor = Color.White,
 disabledContainerColor = Color(0xFFB0BEC5),
 disabledContentColor = Color.White
 )
 ) {
 Text(
 "SUBMIT",
 style = MaterialTheme.typography.titleMedium,
 fontWeight = FontWeight.Bold
 )
 }

 if (state.isLoading) {
 Row(
 Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.Center
 ) {
 CircularProgressIndicator(
 modifier = Modifier.padding(top = 8.dp),
 color = Color(0xFF2196F3)
 )
 }
 }

 state.error?.let {
 Text(
 it,
 color = MaterialTheme.colorScheme.error,
 modifier = Modifier.fillMaxWidth(),
 textAlign = TextAlign.Center
 )
 }

 Spacer(modifier = Modifier.height(32.dp))
 }
 }

 // Success Dialog
 if (showDialog) {
 AlertDialog(
 onDismissRequest = { showDialog = false },
 title = { Text("Currency converted") },
 text = { Text(dialogMessage) },
 confirmButton = {
 TextButton(onClick = { showDialog = false }) {
 Text("Done")
 }
 }
 )
 }

 // Network connectivity dialog
 if (state.showNetworkDialog) {
 AlertDialog(
 onDismissRequest = { vm.dismissNetworkDialog() },
 title = { Text("No Internet Connection") },
 text = {
 Text("Please check your internet connection and try again. Exchange rates cannot be updated without an internet connection.")
 },
 confirmButton = {
 TextButton(onClick = { vm.retryNetworkOperation() }) {
 Text("Retry")
 }
 },
 dismissButton = {
 TextButton(onClick = { vm.dismissNetworkDialog() }) {
 Text("Cancel")
 }
 }
 )
 }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun ExchangeCard(
 label: String,
 amount: String,
 onAmountChanged: (String) -> Unit,
 currency: String,
 currencies: List<String>,
 onCurrencyChanged: (String) -> Unit,
 isReadOnly: Boolean
) {
 Column(
 modifier = Modifier
 .fillMaxWidth()
 .clip(RoundedCornerShape(16.dp))
 .background(Color.White)
 .padding(20.dp),
 verticalArrangement = Arrangement.spacedBy(12.dp)
 ) {
 Text(
 text = label,
 style = MaterialTheme.typography.titleSmall,
 fontWeight = FontWeight.Medium,
 color = Color(0xFF5D6D7E)
 )

 Row(
 modifier = Modifier.fillMaxWidth(),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 // Amount Input
 if (isReadOnly) {
 Text(
 text = amount,
 style = MaterialTheme.typography.headlineMedium,
 fontWeight = FontWeight.Bold,
 color = Color(0xFF2C3E50),
 modifier = Modifier.weight(1f)
 )
 } else {
 TextField(
 value = amount,
 onValueChange = onAmountChanged,
 placeholder = {
 Text(
 "0.00",
 style = MaterialTheme.typography.headlineMedium,
