 "0.00",
 style = MaterialTheme.typography.titleLarge,
 color = Color(0xFFBDC3C7),
 fontSize = 22.sp
 )
 },
 textStyle = MaterialTheme.typography.titleLarge.copy(
 fontWeight = FontWeight.Bold,
